mapper.select:
        SELECT * FROM POINTTYPE WHERE systemtype != '0010' 


        	and seq = #{seq}



	    	and delyn is null



	    	and delyn = #{delYn}



    		and bigtype = #{bigType}



    		and systemtype = #{systemType}



    		and typename LIKE CONCAT ('%',#{typeName},'%')



    		and plusminus = #{plusMinus}


        order by seq asc

		limit #{pageStart}, #{perPageNum}

